import React, { useState, useEffect } from 'react'
import { Link, useParams, useNavigate, createSearchParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Image, ListGroup, Button, Form, ListGroupItem } from 'react-bootstrap'
import { listProductDetails } from '../actions/productActions'


function ItemScreen({ }) {
  const [qty, setQty] = useState(1);
  const params = useParams();
  let navigate = useNavigate();
  const dispatch = useDispatch();

  const productDetails = useSelector(state => state.productDetails);
  const { product } = productDetails;

  const colWFull = {span: 10, offset: 1}


  useEffect(() => {
    dispatch(listProductDetails(`${params.id}`))

  }, [dispatch, params])



  const addToCartEventHandler = ((id, qty) => {
    navigate(`/cart/${id}`, { state: qty })

  })

  return (
    <div>

      <Link to='/' className='btn btn-light my-3'>Indietro</Link>

      <Row>
        

        <Col sm={12} md={colWFull} lg={colWFull} xl={colWFull}>
          <h1>{product.name}</h1>
          <Image src={product.image} fluid/>
        </Col>

      </Row>

      <Row>

        <Col md={6}>
          <ListGroup variant='flush'>   

            <ListGroup.Item className='c-padding-list'>
              <h5>{product.description}</h5>
            </ListGroup.Item>

            <ListGroup.Item className='c-padding-list'>
              <Row>
                <Col>Price:</Col>
                <Col>${product.price}</Col>
              </Row>
            </ListGroup.Item>

            <ListGroup.Item  className='c-padding-list'>
              <Row>
                <Col>Status:</Col>
                <Col>{product.countInStock > 1 ? 'Disponibile' : 'Esaurito'}</Col>
              </Row>
            </ListGroup.Item>

            {product.countInStock > 0 && (
              <ListGroup.Item className='c-padding-list'>
                <Row>
                  <Col>Qty</Col>
                  <Col xs='auto' className='my-1'>
                    <Form.Control
                      as="select"
                      value={qty}
                      onChange={(e) => setQty(e.target.value)}
                    >
                      {

                        [...Array(product.countInStock).keys()].map((x) => (
                          <option key={x + 1} value={x + 1}>
                            {x + 1}
                          </option>
                        ))
                      }

                    </Form.Control>
                  </Col>
                </Row>
              </ListGroup.Item>
            )}

            <ListGroup.Item  className='c-padding-list'>
              <Row>
                <Button className='btn-block'
                  onClick={() => addToCartEventHandler(`${params.id}`, qty)}
                  type='button' disabled={
                    product.countInStock === 0}>Aggiungi al carrello</Button>
              </Row>
            </ListGroup.Item>

          </ListGroup>
        </Col>

      </Row>

    </div>

  )
}

export default ItemScreen
